mapper.select:
select oj_id
from oj
where username = #{username} and platform = #{platform}

mapper.update:
update oj
set now_rating = #{nowRating}
where username = #{username} and platform = #{platform} and oj_id = #{ojId}

update oj
set history_rating = #{historyRating}
where username = #{username} and platform = #{platform} and oj_id = #{ojId}
